+    }
+
+    fn reflect_short_type_path(&self) -> &str {
+        Self::short_type_path()
+    }
+
+    fn reflect_type_ident(&self) -> Option<&str> {
+        Self::type_ident()
+    }
+
+    fn reflect_crate_name(&self) -> Option<&str> {
+        Self::crate_name()
+    }
+
+    fn reflect_module_path(&self) -> Option<&str> {
+        Self::module_path()
+    }
+}
